<script>
    // Checkout details as a json
    var options = {"key":"rzp_test_ZqITQjmEGb89M2","amount":150000,"name":"RoarHomz","description":"Rooms On Affordable Rates","image":"https:\/\/www.roarhomz.com\/images\/favicon.png","prefill":{"name":"Test","email":"test@gmail.com","contact":"1234567898"},"notes":{"address":"1","merchant_order_id":"8324"},"theme":{"color":"#F37254"},"order_id":"order_NM7aDIn8cAtyRr"};

    /**
     * The entire list of Checkout fields is available at
     * https://docs.razorpay.com/docs/checkout-form#checkout-fields
     */
    options.handler = function (response) {
        document.getElementById('razorpay_payment_id').value = response.razorpay_payment_id;
        document.getElementById('razorpay_signature').value = response.razorpay_signature;
        document.razorpayform.submit();
    };

    // Boolean whether to show image inside a white frame. (default: true)
    options.theme.image_padding = false;

    options.modal = {
        ondismiss: function () {
            console.log("This code runs when the popup is closed");
        },
        // Boolean indicating whether pressing escape key 
        // should close the checkout form. (default: true)
        escape: true,
        // Boolean indicating whether clicking translucent blank
        // space outside checkout form should close the form. (default: false)
        backdropclose: false
    };

    var rzp = new Razorpay(options);
    rzp.open();
